import sysimport jsonimport osfrom datetime import datetimedef update_notifications(input_file, output_file, report_name, status, notes=None):    # Create a new entry with the current timestamp, report_name, and status    new_entry = {        "report_name": report_name,        "status": status,        "timestamp": datetime.now().strftime("%Y-%m-%dT%H:%M:%S")    }    # Include notes only if provided    if notes:        new_entry["notes"] = notes    # Check if the input file exists and read its content    if os.path.exists(input_file) and os.path.getsize(input_file) > 0:        with open(input_file, 'r') as file:            data = json.load(file)    else:        data = []    # Append the new entry    data.append(new_entry)    # Write the updated data to the output file    with open(output_file, 'w') as file:        json.dump(data, file, indent=4)if __name__ == "__main__":    if len(sys.argv) < 5 or len(sys.argv) > 6:        print("Usage: python3 update_notifications.py <input_file> <output_file> <report_name> <status> [notes]")        sys.exit(1)    input_file = sys.argv[1]    output_file = sys.argv[2]    report_name = sys.argv[3]    status = sys.argv[4]    notes = sys.argv[5] if len(sys.argv) > 5 else None    update_notifications(input_file, output_file, report_name, status, notes)