class CredentialError(Exception):    """Base class for credential-related errors"""class PasswordExpiredError(CredentialError):    """Password expired detected via UI"""class LoginError(CredentialError):    """Invalid login detected via UI"""class TransientExtractionError(Exception):    """    Non-credential failure:    UI glitch, timeout, image not found, website instability    """class MaxRetriesExceeded(Exception):    """Raised when retry limit is exceeded for a valid user"""